import React, { Component } from 'react'

export default class StateInClass extends Component {
    constructor(props){
        super(props);
        this.state = {
            id: props.myId,
            name: props.name,
            counter: 0
        };

    }
    render() {
    return (
    <div>
        
        <p>Name: {this.state.name}</p>
        <p>Counter: {this.state.counter}</p>
    <button onClick={this.click}>+</button>
    <button>-</button>
    </div>
    );
    }
}
